before_layer_change_gcode:
; before layer [layer_num] change
{if layer_z <= initial_layer_print_height + layer_height * 2}
M140 S{max(bed_temperature_initial_layer_single, bed_temperature_initial_layer_single)}
{else}
M140 S{max(bed_temperature[0], bed_temperature[1])}
{endif}
{if (filament_type[0] =="PLA" or filament_type[0] =="PETG")}
{if layer_z >= initial_layer_print_height + layer_height * 2}
M106 P2 S150
{elsif layer_z >= initial_layer_print_height + layer_height * 1}
M106 P2 S100
{else}
M106 P2 S0
{endif}
{endif}
G92 E0
